import React, { Component, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import $ from 'jquery'


const UserInfo = (props) => {

  const [user, setuser] = useState(useSelector(state => state.user));
  const is_followed = useSelector(state => state.user.is_followed)
  const access = useSelector(state => state.user.access)
  const dispatch = useDispatch()


  $.ajax({
    url: "https://app165.acapp.acwing.com.cn/myspace/getinfo/",
    type: "GET",
    data: {
      user_id: props.user_id,
    },
    headers: {
      'Authorization': 'Bearer ' + access
    },
    success(resp) {

      setuser({
        username: resp.username,
        id: resp.id,
        photo: resp.photo,
        is_followed: resp.is_followed,
        followCount: resp.followerCount,
      })
    }
  })

  const follow = () => {
    if (is_followed) {
      return "followed"
    } else {
      return "+follow"
    }
  }

  const handleClick = () => {
    
    is_followed = !is_followed
  }

  return ( 
    <div className="card">
      <div className="card-body">
        <div className="row">
          <div className="col-3">
            <img className="img-fluid" style={{borderRadius: "50%"}} src={user.photo} alt="" />
          </div>
          <div className="col-9">
            <div className="username" style={{fontWeight: "bold"}}>{user.username}</div>
            <div className="fans" style={{fontSize: "12px", color: "gray"}}>fans: {user.followCount}</div>
            <button onClick={handleClick} className="btn btn-secondary btn-sm" style={{border: "0px", padding: "2px 4px", fontSize: "12px"}}>{follow()}</button>
          </div>
        </div>
      </div>
    </div>

  );
}
 
export default UserInfo
