        //     success(resp) {
        //       dispatch({
        //         type: 'updateAccess',
        //         access
        //       })
        //     }
        //   })
        // }, 4.5*60*1000);
  
      //   $.ajax({
      //     url: "https://app165.acapp.acwing.com.cn/myspace/getinfo/",
      //     type: "GET",
      //     data: {
      //       user_id: access_obj.user_id,
      //     },
      //     headers: {
      //       'Authorization': 'Bearer ' + access,
      //     },
      //     success(resp) {
      //       console.log(resp)
      //       dispatch({
      //         type: "updateUser",
      //         username: resp.username,
      //         id: resp.id,
