  return ( 
    <Base>
      <div className="row">
        <div className="col-3">
          <img className="img-fluid" style={{borderRadius: "50%"}} src="https://cdn.acwing.com/media/user/profile/photo/85481_lg_140f816091.jpg" alt="" />
        </div>
        <div className="col-9">
          <div className="username">{username}</div>
          <div className="fans">fans: {fansCount}</div>
          <button className="btn btn-secondary btn-sm">follow</button>
        </div>
      </div>
    </Base>
  );
}
 
export default UserInfo
