import React, { Component, useState } from 'react';
import Base from './base';
import UserInfo from './userdynamics/userinfo';
import UserPost from './userdynamics/userpost';
import UserWrite from './userdynamics/userwrite';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Userdynamics = () => {

  let access = useSelector(state => state.user.access)
  const [done, setdone] = useState(false)
  const user_id = useParams().userid;
  const [postCount, setpostCount] = useState(3)
  const [posts, setposts] = useState([])

  const getposts = (user_id, access) => {
    if (!done) {
      $.ajax({
        url: "https://app165.acapp.acwing.com.cn/myspace/post/",
        type: "GET",
        data: {
          user_id,
        },
        headers: {
          'Authorization': 'Bearer ' + access
        },
        success(resp) {
          setposts({
            username: resp.username,
            id: resp.id,
            photo: resp.photo,
            is_followed: resp.is_followed,
            followCount: resp.followerCount,
          })
          setdone(true)
        }
      })
    }
  }

  const post_a_post = (content) => {
    setpostCount(postCount + 1);  
    let post = {
      id: postCount,
      userid: 1,
      content: content,
    }
    setposts([...posts, post]);
  }

  const delete_a_post = (post_id) => {

    const newposts = posts.filter(post => post.id !== post_id);
    setpostCount(postCount - 1)
    setposts(newposts);
  }

  return ( 
    <Base>
      <div className="row">
        <div className="col-3">
          <UserInfo user_id={user_id} />
          <UserWrite posts={posts} postCount={postCount} post_a_post={post_a_post} />
        </div>
        <div className="col-9">
          <UserPost posts={posts} postCount={postCount} delete_a_post={delete_a_post} />
        </div>
      </div>
    </Base>
  );
}

export default Userdynamics
