import React, { Component, useState } from 'react';
import Base from '../base';

const UserInfo = () => {
  const [username, setusername] = useState('hyy678');
  const [fansCount, setfansCount] = useState(0);
  const [is_followed, setis_followed] = useState(false);

  const btn = () => {
    if (is_followed) {
      console.log("follow")
    } else {
      console.log("followed")
    }
  }

  return ( 
    <Base>
      <div className="row">
        <div className="col-3">
          <img className="img-fluid" style={{borderRadius: "50%"}} src="https://cdn.acwing.com/media/user/profile/photo/85481_lg_140f816091.jpg" alt="" />
        </div>
        <div className="col-9">
          <div className="username">{username}</div>
          <div className="fans">fans: {fansCount}</div>
          <button onClick={btn} className="btn btn-secondary btn-sm">follow</button>
        </div>
      </div>
    </Base>
  );
}
 
export default UserInfo
