import actions from './action';
import jwt_decode from 'jwt-decode'
import $ from 'jquery'

const reducer = (state = {
  user: {
    username: "asdfasdfas",
    id: 1,
    photo: "",
    followCount: 0,
    access: "",
    refresh: "",
    is_login: false,
  }
}, action) => {
  switch(action.type) {
    case actions.Login:
      $.ajax({
        url: "https://app165.acapp.acwing.com.cn/api/token/",
        type: "POST",
        data: {
          username: action.username,
          password: action.password,
        },
        success(resp) {
          console.log(resp)
        },
      })
    default:
      return state;
  }
}

export default reducer
