import React, { Component, useState } from 'react';
import Base from './base';
import UserInfo from './userdynamics/userinfo';
import UserPost from './userdynamics/userpost';
import UserWrite from './userdynamics/userwrite';
import { useParams } from 'react-router-dom';

const Userdynamics = () => {

  const user_id = useParams();
  console.log(user_id.user_id)
  const [postCount, setpostCount] = useState(3)
  const [posts, setposts] = useState([
    {
      id: 0,
      userid: 1,
      content: 'a happy day',
    },
    {
      id: 1,
      userid: 2,
      content: 'a happy day',
    },
    {
      id: 2,
      userid: 3,
      content: 'a happy day',
    },
  ])



  const post_a_post = (content) => {
    setpostCount(postCount + 1);  
    let post = {
      id: postCount,
      userid: 1,
      content: content,
    }
    setposts([...posts, post]);
  }

  const delete_a_post = (post_id) => {

    const newposts = posts.filter(post => post.id !== post_id);
    setpostCount(postCount - 1)
    setposts(newposts);
  }

  return ( 
    <Base>
      <div className="row">
        <div className="col-3">
          <UserInfo user_id={user_id.user_id} />
          <UserWrite posts={posts} postCount={postCount} post_a_post={post_a_post} />
        </div>
        <div className="col-9">
          <UserPost posts={posts} postCount={postCount} delete_a_post={delete_a_post} />
        </div>
      </div>
    </Base>
  );
}

export default Userdynamics
