import React, { Component, useState } from 'react';
import Base from './base';
import { useDispatch } from 'react-redux'
import { history } from 'umi'

const Login = () => {

  const [error_message, seterror_message] = useState('')
  const [username, setusername] = useState('');
  const [password, setpassword] = useState('')
  const dispatch = useDispatch();

  const login = () => {
    dispatch({
      type: "login",
      username: username,
      password: password,
      success() {
        error_message = '';
        history.push('/friends')
      },
      error() {
        error_message = 'username or password error!'
      }
    })
  }

  return ( 
    <Base>
      <div className="row justify-content-md-center">
      <div className="col-4">
        <div>
          <div className="mb-3">
            <label htmlFor="username" className="form-label">Username</label>
            <input onChange={e => {setusername(e.target.value)}} type="text" className="form-control" id="username" aria-describedby="emailHelp" />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password</label>
            <input onChange={e => {setpassword(e.target.value)}} type="password" className="form-control" id="password" />
          </div>
          <div className="error" style={{color:"red", float:"right"}}>{error_message}</div>
          <button onClick={login} type="submit" className="btn btn-primary" style={{width:"100%", marginTop:"10px"}}>Login</button>
        </div>
      </div>
    </div>
    </Base>
  );
}

export default Login
